package leetcode

import org.assertj.core.api.Assertions.assertThat
import org.junit.jupiter.api.Test

class LC_647_PalindromicSubstringsTest {

	private val lc647 = LC_647_PalindromicSubstrings()

	@Test
	fun countSubstrings() {
		assertThat(lc647.countSubstrings("abc")).isEqualTo(3)
		assertThat(lc647.countSubstrings("aaa")).isEqualTo(6)
		assertThat(lc647.countSubstrings("abbcbba")).isEqualTo(12)
		assertThat(lc647.countSubstrings("")).isEqualTo(1664)
	}
}